import { cn } from "@/lib/utils";
import { Landmark, Home, Calendar, Calculator, CircleDollarSign, BarChart, AreaChart, Settings, TrendingUp, BookOpen } from "lucide-react";

type SideMenuVariant = "he" | "ps" | "tr";

interface SideMenuProps {
  variant: SideMenuVariant;
}

const menuItems = {
  ps: [
    { label: "Corp Reports", icon: Landmark },
    { label: "Site Reports", icon: Home },
    { label: "Daily", icon: Calendar },
    { label: "Budget", icon: CircleDollarSign },
    { label: "Forecast", icon: TrendingUp },
    { label: "Accounting", icon: Calculator },
    { label: "ProfitPace", icon: AreaChart },
    { label: "ProfitPlan", icon: BarChart },
    { label: "Admin", icon: Settings },

  ],
  he: [
    { label: "Budget", icon: Calculator },
    { label: "Forecast", icon: Calculator },
  ],
  tr: [
    { label: "Admin", icon: Settings },
  ],
  footer: [{ label: "Help", icon: BookOpen }, { label: "Admin", icon: Settings },],
};

export function SideMenu({ variant }: SideMenuProps) {
  const variantItems = menuItems[variant] || [];

  return (
    <div
      className={cn(
        "bg-sidebar-background group-data-[variant=floating]:border-sidebar-border flex h-full min-h-0 flex-col p-1",
        variant === "he" && "w-15",
        variant === "ps" && "w-24 bg-white pt-2",
        variant === "tr" && "w-12"
      )}
    >
      <div className="flex min-h-0 flex-1 flex-col gap-2 overflow-auto">
        {variantItems.map((item, index) => (
          <span
            key={index}
            className="flex flex-col items-center justify-start w-full px-2 py-2 rounded-md text-sidebar-foreground hover:text-indigo-700 hover:bg-indigo-100 cursor-pointer"
          >
            <a href="#" className="flex flex-col items-center">
              <item.icon className="h-5 w-5 mb-1" />
              <span className="text-[11px] text-center font-semibold tracking-tight">{item.label}</span>
            </a>
          </span>
        ))}
      </div>
      <div className="flex flex-col gap-2">
        {menuItems.footer.map((item, index) => (
           <span
            key={index}
            className="flex flex-col items-center justify-start w-full px-2 py-2 rounded-md text-sidebar-foreground hover:text-indigo-700 hover:bg-indigo-100 cursor-pointer"
          >
            <a href="#" className="flex flex-col items-center">
              <item.icon className="h-5 w-5 mb-1" />
              <span className="text-[11px]">{item.label}</span>
            </a>
          </span>
        ))}
      </div>
    </div>
  );
}

"use client";

import * as React from "react";
import {
  ColumnDef,
  ColumnFiltersState,
  SortingState,
  VisibilityState,
  flexRender,
  getCoreRowModel,
  getFilteredRowModel,
  getPaginationRowModel,
  getSortedRowModel,
  useReactTable,
} from "@tanstack/react-table";
import { ArrowPathIcon, EllipsisHorizontalIcon, PlusIcon, PrinterIcon } from '@heroicons/react/16/solid';
import { ChevronDown } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Input } from "@/components/ui/input";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { toast } from "sonner";

interface DataTableProps<TData, TValue> {
  columns: ColumnDef<TData, TValue>[]
  data: TData[]
}

export function DataTable<TData, TValue>({
  columns,
  data,
}: DataTableProps<TData, TValue>) {
  const [sorting, setSorting] = React.useState<SortingState>([]);
  const [columnFilters, setColumnFilters] = React.useState<ColumnFiltersState>(
    []
  );
  const [columnVisibility, setColumnVisibility] =
    React.useState<VisibilityState>({});
  const [rowSelection, setRowSelection] = React.useState({});

  const table = useReactTable({
    data,
    columns,
    onSortingChange: setSorting,
    onColumnFiltersChange: setColumnFilters,
    getCoreRowModel: getCoreRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    onColumnVisibilityChange: setColumnVisibility,
    onRowSelectionChange: setRowSelection,
    state: {
      sorting,
      columnFilters,
      columnVisibility,
      rowSelection,
    },
  });

  return (
    <div className="w-full">
      <div className="flex items-center pt-1 pb-4 gap-2">
        <Button
          variant="default"
          size="sm"
          startIcon={<PlusIcon />}
          onClick={() => toast.success("Task has been created")}
        >
          Create Task
        </Button>
        <Input
          placeholder="Search task..."
          value={(table.getColumn("taskName")?.getFilterValue() as string) ?? ""}
          onChange={(event) =>
            table.getColumn("taskName")?.setFilterValue(event.target.value)
          }
          className="ml-auto max-w-xs"
        />
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button variant="outline" size="sm" className="">
              Columns <ChevronDown />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end">
            {table
              .getAllColumns()
              .filter((column) => column.getCanHide())
              .map((column) => {
                return (
                  <DropdownMenuCheckboxItem
                    key={column.id}
                    className="capitalize"
                    checked={column.getIsVisible()}
                    onCheckedChange={(value) =>
                      column.toggleVisibility(!!value)
                    }
                  >
                    {column.id}
                  </DropdownMenuCheckboxItem>
                );
              })}
          </DropdownMenuContent>
        </DropdownMenu>
        <div className="gap-1">
          <Button
            variant="ghost"
            size="icon-sm"
            startIcon={<ArrowPathIcon />}
          />
          <Button
            variant="ghost"
            size="icon-sm"
            startIcon={<PrinterIcon />}
          />

          <Button
            variant="ghost"
            size="icon-sm"
            startIcon={<EllipsisHorizontalIcon />}
          />
        </div>
      </div>
      <div className="rounded-md border">
        <Table>
          <TableHeader>
            {table.getHeaderGroups().map((headerGroup) => (
              <TableRow key={headerGroup.id}>
                {headerGroup.headers.map((header) => {
                  return (
                    <TableHead key={header.id} className="[&:has([role=checkbox])]:pl-4">
                      {header.isPlaceholder
                        ? null
                        : flexRender(
                            header.column.columnDef.header,
                            header.getContext()
                          )}
                    </TableHead>
                  );
                })}
              </TableRow>
            ))}
          </TableHeader>
          <TableBody>
            {table.getRowModel().rows?.length ? (
              table.getRowModel().rows.map((row) => (
                <TableRow
                  key={row.id}
                  data-state={row.getIsSelected() && "selected"}
                >
                  {row.getVisibleCells().map((cell) => (
                    <TableCell
                      key={cell.id}
                      className="[&:has([role=checkbox])]:pl-4"
                    >
                      {flexRender(
                        cell.column.columnDef.cell,
                        cell.getContext()
                      )}
                    </TableCell>
                  ))}
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell
                  colSpan={columns.length}
                  className="h-24 text-center"
                >
                  No results.
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>
      <div className="flex items-center justify-end space-x-2 py-4">
        <div className="flex-1 text-sm text-muted-foreground">
          {table.getFilteredSelectedRowModel().rows.length} of{" "}
          {table.getFilteredRowModel().rows.length} row(s) selected.
        </div>
        <div className="space-x-2">
          <Button
            variant="outline"
            size="sm"
            onClick={() => table.previousPage()}
            disabled={!table.getCanPreviousPage()}
          >
            Previous
          </Button>
          <Button
            variant="outline"
            size="sm"
            onClick={() => table.nextPage()}
            disabled={!table.getCanNextPage()}
          >
            Next
          </Button>
        </div>
      </div>
    </div>
  );
}

"use client";

import { ColumnDef } from "@tanstack/react-table";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { MoreHorizontal } from "lucide-react";
import { ChevronUpDownIcon } from '@heroicons/react/16/solid';

export type Task = {
  id: string;
  taskName: string;
  status: "Pending" | "In Progress" | "Done";
  dueDate: string;
  assignedTo: string;
  priority: "Low" | "Medium" | "High";
};

export const columns: ColumnDef<Task>[] = [
  {
    id: "select",
    header: ({ table }) => (
      <Checkbox
        checked={
          table.getIsAllPageRowsSelected() ||
          (table.getIsSomePageRowsSelected() && "indeterminate")
        }
        onCheckedChange={(value) => table.toggleAllPageRowsSelected(!!value)}
        aria-label="Select all"
      />
    ),
    cell: ({ row }) => (
      <Checkbox
        checked={row.getIsSelected()}
        onCheckedChange={(value) => row.toggleSelected(!!value)}
        aria-label="Select row"
      />
    ),
    enableSorting: false,
    enableHiding: false,
  },
  {
    accessorKey: "taskName",
    header: ({ column }) => {
      return (
        <Button
          variant="ghost"
          className="font-semibold text-neutral-700 hover:text-neutral-800 p-1 h-8 focus:ring-0"
          onClick={() =>
            column.toggleSorting(column.getIsSorted() === "asc")
          }
        >
          Task
          <ChevronUpDownIcon className="size-4"/>
        </Button>
      );
    },
    cell: ({ row }) => (
      <div className="capitalize">{row.getValue("taskName")}</div>
    ),
  },
  {
    accessorKey: "status",
    header: "Status",
    cell: ({ row }) => {
      const status = row.getValue("status") as Task["status"];
      let badgeColor: "yellow" | "blue" | "green" | "neutral" = "neutral";
      if (status === "Pending") badgeColor = "yellow";
      else if (status === "In Progress") badgeColor = "blue";
      else if (status === "Done") badgeColor = "green";
      return (
        <Badge variant="outline" color={badgeColor}>
          {status}
        </Badge>
      );
    },
  },
  {
    accessorKey: "dueDate",
    header: ({ column }) => {
      return (
        <Button
          variant="ghost"
          className="font-semibold text-neutral-700 hover:text-neutral-800 p-1 h-8 focus:ring-0"
          onClick={() =>
            column.toggleSorting(column.getIsSorted() === "asc")
          }
        >
          Due Date
          <ChevronUpDownIcon className="size-4"/>
        </Button>
      );
    },
    cell: ({ row }) => (
      <div className="capitalize">{row.getValue("dueDate")}</div>
    ),
  },
  {
    accessorKey: "assignedTo",
    header: ({ column }) => {
      return (
        <Button
          variant="ghost"
          className="font-semibold text-neutral-700 hover:text-neutral-800 p-1 h-8 focus:ring-0"
          onClick={() =>
            column.toggleSorting(column.getIsSorted() === "asc")
          }
        >
        Assigned To
        <ChevronUpDownIcon className="size-4"/>
      </Button>
      );
    },
    cell: ({ row }) => <div>{row.getValue("assignedTo")}</div>,
  },
  {
    accessorKey: "priority",
    header: "Priority",
    cell: ({ row }) => {
      const priority = row.getValue("priority") as Task["priority"];
      let badgeColor: "red" | "orange" | "green" = "green";
      if (priority === "High") badgeColor = "red";
      else if (priority === "Medium") badgeColor = "orange";
      return (
        <Badge variant="solid" color={badgeColor}>
          {priority}
        </Badge>
      );
    },
  },
  {
    id: "actions",
    enableHiding: false,
    cell: ({ row }) => {
      const task = row.original;

      return (
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button variant="ghost" className="h-8 w-8 p-0">
              <span className="sr-only">Open menu</span>
              <MoreHorizontal />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end">
            <DropdownMenuLabel>Actions</DropdownMenuLabel>
            <DropdownMenuItem
              onClick={() => navigator.clipboard.writeText(task.id)}
            >
              Copy task ID
            </DropdownMenuItem>
            <DropdownMenuSeparator />
            <DropdownMenuItem>View project</DropdownMenuItem>
            <DropdownMenuItem>View details</DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      );
    },
  },
];
