:root {
  --radius: 0.625rem;
  --background: var(--color-white);
  --foreground: var(--color-neutral-900);
  --card: var(--color-white);
  --card-foreground: var(--color-neutral-800);
  --popover: var(--color-white);
  --popover-foreground: var(--color-neutral-900);
  --primary: var(--color-indigo-600);
  --primary-foreground: var(--color-white);
  --secondary: var(--color-neutral-200);
  --secondary-foreground: var(--color-neutral-800);
  --muted: var(--color-neutral-50);
  --muted-foreground: var(--color-neutral-800);
  --accent: var(--color-indigo-100);
  --accent-foreground: var(--color-indigo-700);
  --destructive: var(--color-red-700);
  --border: oklch(0.922 0 0);
  --input: var(--color-neutral-300);
  --ring: var(--color-indigo-700);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
  }
}
