@layer components {
  [data-sonner-toast] {
    @apply !p-4 !gap-4 
  }

  [data-sonner-toast] [data-icon] {
    @apply bg-secondary/25 ml-2 outline-16 outline-solid outline-secondary/25 rounded-full size-9 justify-center
  }

  .card {
    @apply bg-gray-100 rounded-lg shadow-md p-4; /* Default styles */
  }
  .btn {
    @apply px-4 py-2 rounded-md text-white bg-blue-500 hover:bg-blue-700; /* Default button styles */
